import React, { Component } from 'react';
import NewGame from '../components/NewGame'

class Game extends Component {

    state = {
        questions: [],
        currentQuiz:[]
    }

    componentDidMount() {
        fetch('http://localhost:3000/questions')
        .then(res => res.json())
        .then(questions => {
            this.setState({
                questions: questions,
                currentQuiz: this.getFourQuestions(questions)
            })
        })
    }

    getFourQuestions = (questions) => {
        let fourQuestions = []
        //use a while loop if we are checking for repeat question or filter or slice
        for (let i = 0; i < 4; i++) {
            let question = questions[Math.floor(Math.random()*questions.length)]
            fourQuestions.push(question)
        }
        return fourQuestions
    }

    newQuiz = (questions) => {
        this.setState({
            currentQuiz: this.getFourQuestions(questions)
        })
    }

    render() {
        console.log(this.state.currentQuiz)
        return (
            <div>
                <h1>Lutrivia</h1>
                <NewGame 
                    questions = { this.state.questions }
                    newQuiz = { this.newQuiz }
                />
            </div>
        )
    }
}

export default Game;
