import React from 'react'

export default class QuestionCard extends React.Component {

    state = {
        selectedAns: null
    }

    selectAns = (value) => {
        this.setState({
            selectedAns: value
        })
    }

    render() {
        console.log(this.state.selectedAns, this.props.question)
        return(
            <div>
                Question
                <p>{this.props.question.text}</p>
                <button onClick = {() => this.selectAns(true) }>
                    True
                </button>
                <button onClick = {() => this.selectAns(false) }>
                    False
                </button>
            </div>
        )
    }
}
