import React from 'react'

export default class QuestionCard extends React.Component {

    state = {
        selectedAns: false
    }

    selectAns = (e, value) => {
        let right = this.props.question.answer === value;
        let color;

        if (right) {
			color = 'green';
		} else {
			color = 'red'
		}
		e.target.style.backgroundColor = color;

        this.setState({
            selectedAns: true
        })
    }

    render() {
        return(
            <div>
                Question
                <p>{this.props.question.text}</p>
                <button className = 'btn' onClick = {(e) => this.selectAns(e, true) } disabled={this.state.selectedAns}>
                    True
                </button>
                <button className = 'btn' onClick = {(e) => this.selectAns(e, false) } disabled={this.state.selectedAns}>
                    False
                </button>
            </div>
        )
    }
}
