import React, { Component } from 'react';
import NewGame from '../components/NewGame'
import QuestionList from './QuestionList'
class Game extends Component {

    state = {
        questions: [],
        currentQuiz:[],
        points:0
    }

    componentDidMount() {
        fetch('http://localhost:3000/questions')
        .then(res => res.json())
        .then(questions => {
            this.setState({
                questions: questions,
                currentQuiz: this.getFourQuestions(questions)
            })
        })
    }

    getFourQuestions = (questions) => {
        let fourQuestions = []
        //use a while loop if we are checking for repeat question or filter or slice
        for (let i = 0; i < 4; i++) {
            let question = questions[Math.floor(Math.random()*questions.length)]
            fourQuestions.push(question)
        }
        return fourQuestions
    }

    newQuiz = (questions) => {
        this.setState({
            currentQuiz: this.getFourQuestions(questions)
        })
    }

    increasePoints = () => {
        this.setState({
            points:this.state.points + 1
        })
    }

    increasePoints = () => {
		this.setState((state, _props) => {
			return {
				score: state.score + 1
			}
		})
	}

    render() {
        console.log(this.state.currentQuiz)
        return (
            <div>
                <h1>Lutrivia</h1>
                <h2>{this.state.points}</h2>
                <NewGame 
                    questions = { this.state.questions }
                    newQuiz = { this.newQuiz }
                />
                <QuestionList 
                    currentQuiz = { this.state.currentQuiz }
                    increasePoints = { this.increasePoints }
                />
            </div>
        )
    }
}

export default Game;
